import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Button, Container, Row } from 'reactstrap';


class MusicPlayer extends Component {
    constructor(props) {
    super(props);
    this.state = {
      currentSong: props.currentSong,
      currentTitle: props.title,
      musicLocationArray: [
        {
          songName:'Would You Ever',
          artist: 'Skrillex',
          songLocation: 'https://firebasestorage.googleapis.com/v0/b/reverb-9081f.appspot.com/o/Skrillex%20%26%20Poo%20Bear%20-%20Would%20You%20Ever.mp3?alt=media&token=1de62308-3d33-44cc-95aa-443ff5953d2a'
        },
        {
          songName:'Bohemian Rhapsody',
          artist: 'Queen',
          songLocation: 'https://firebasestorage.googleapis.com/v0/b/reverb-9081f.appspot.com/o/rizkyrilos%20-%20Queen%20-%20Bohemian%20Rhapsody.mp3?alt=media&token=56ed2628-668d-4546-a657-692f4d51899e'
        }
      ],
      play: false,
      pause: true,
    }
    this.changeSong = this.changeSong.bind(this);
  }
  async changeSong(songLocation, songTitle, songArtist) {
    await this.setState({currentSong: songLocation});
    await this.setState()
    document.getElementById("music-player").pause();
    document.getElementById("music-player").setAttribute('src', this.state.currentSong);
    document.getElementById("music-player").load();
    document.getElementById("music-player").play();
    this.props.complete(songLocation);

  }

  componentDidMount() {
    setInterval(() => {
      this.setState(() => {
        console.log('Setting state');
        return { unseen: "does not display"}
      });
    }, 1000);
  }

  render() {
    console.log("The prop is", this.state.currentSong);
    console.log("Render called");
    return (
      <div>
      <Row>
          <audio id="music-player" controls>
            <source src={this.state.currentSong}/>
          </audio>

      </Row>
      <br />
      <br />
      <Button onClick={() => {
        this.changeSong('https://firebasestorage.googleapis.com/v0/b/reverb-9081f.appspot.com/o/rizkyrilos%20-%20Queen%20-%20Bohemian%20Rhapsody.mp3?alt=media&token=56ed2628-668d-4546-a657-692f4d51899e')
      }}>Bohemian Rhapsody</Button>
      <div />
      <Button onClick={()=> {
        this.changeSong('https://firebasestorage.googleapis.com/v0/b/reverb-9081f.appspot.com/o/Skrillex%20%26%20Poo%20Bear%20-%20Would%20You%20Ever.mp3?alt=media&token=1de62308-3d33-44cc-95aa-443ff5953d2a')
      }}>Would You Ever</Button>
      </div>
      );
  }
}

MusicPlayer.propTypes = {
  currentSong: PropTypes.string,
}
export default MusicPlayer
