import React, {Component} from 'react';
import { compose } from 'recompose';
import '../App/App.css';
import { Button, Container, Row } from 'reactstrap';
import MusicPlayer from '../MusicPlayer/index.js'

//import { withAuthorization, withEmailVerification } from '../Session';
import { withAuthorization } from '../Session';
import Messages from '../Messages';
//import Songs from '../Songs';



class HomePage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentSong: '',
      currentArtist:'',
      currentTitle:'',
      currentTime: 0,

    };
    this.handleSongChange = this.handleSongChange.bind(this);
  }
  handleSongChange(newSong) {
    this.setState({currentSong: newSong});
    console.log("YEEERRRRRR");
    console.log(this.state.currentSong);
  }


  render() {
    return(

        <Container id="intro">
          <h1>Your Music Player</h1>
          <MusicPlayer
          currentSong={this.state.currentSong}
          currentTitle={this.state.currentTitle}
          complete = {(newSong) => this.handleSongChange(newSong)}
          />
          <br />
          <Messages
          currentSong={this.state.currentSong}
          complete = {(streamSong) => this.handleSongChange(streamSong)}/>
        </Container>
      )
    }

  }

const condition = authUser => !!authUser;

export default compose(
  withAuthorization(condition),
)(HomePage);
